/**
 * Unify Telegram message & callback into one shape:
 * { chat_id, text, data, is_callback, username, message_id, _raw }
 */
const m = $json.message || {};
const c = $json.callback_query || {};
const isCb = !!c.data;

const chat_id = isCb ? c.message.chat.id : (m.chat?.id);
const text = (m.text || '').trim();
const data = isCb ? c.data : '';
const message_id = isCb ? c.message.message_id : (m.message_id || null);
const username = (m.from?.username || c.from?.username || '') || '';

return [{ json: { chat_id, text, data, is_callback: isCb, message_id, username, _raw: $json } }];

const now = new Date().toISOString();
return [{
  json: {
    CustomerId: $json.chat_id,
    Username: $json.username || '',
    LastSeenAt: now,
    FirstSeenAt: now // will only be used on first insert
  }
}];

const rows = $items('GS: Read Customer').map(i=>i.json);
const id = $items('Normalize Event')[0].json.chat_id;
const idx = rows.findIndex(r => String(r.CustomerId) === String(id));
const existing = idx >= 0 ? rows[idx] : null;
return [{ json: { exists: !!existing, rowIndex: idx+2 /* +2 for header+1-based */, existing } }];

const rows = $items('GS: Read State').map(i=>i.json);
const chat_id = $items('Normalize Event')[0].json.chat_id;
const idx = rows.findIndex(r => String(r.chat_id) === String(chat_id));
const exists = idx >= 0;
const rowIndex = idx+2; // header + 1-based
return [{ json: { chat_id, exists, rowIndex } }];

// Parse Callback ‚Äî robust
// - Supports "ADD|SKU" and "KIND|ACTION|SKU"
// - Harvests callback query id (cqid) from multiple shapes including _raw

const u    = $json;
const raw  = u._raw ?? u;  // your Normalize Event keeps the original payload here
const cb   = raw.callback_query || raw.callbackQuery || raw.callback || null;
const msg  = cb?.message ?? raw.message ?? u.message ?? u;

// Sender / chat
const from = cb?.from ?? msg?.from ?? {};
const chat = msg?.chat ?? {};

// Base fields
const chat_id   = String(chat.id ?? u.chat_id ?? from.id ?? '');
const username  = (from.username ?? u.username ?? '') || '';
const text      = (u.text ?? msg?.text ?? '').trim();

// Callback payload + ID
const data = (u.data ?? cb?.data ?? raw.callback_data ?? '').trim();

// Callback query id (various shapes)
const cqid =
  u.cqid ??
  u.query_id ??
  u.callback_query_id ??
  raw?.query_id ??
  raw?.callback_query_id ??
  cb?.id ??
  null;

const message_id = Number(msg?.message_id ?? u.message_id ?? 0) || 0;

// Parse action/sku
let kind   = '';
let action = '';
let sku    = '';

// ‚úÖ NEW: mark plain text messages explicitly as 'text'
if (!data && text) {
  kind = 'text';
}

if (data) {
  const parts = data.split('|');
  if (parts.length >= 3) {
    [kind, action, sku] = parts;        // KIND|ACTION|SKU
  } else if (parts.length === 2) {
    [action, sku] = parts;               // ACTION|SKU
    kind = 'BTN';
  } else {
    kind = 'BTN';
    action = parts[0] || '';
  }
}

// Optional passthroughs
const page = u.page ?? null;
const arg  = u.arg  ?? null;

// Mark as callback if we have an id OR callback data
const is_callback = Boolean(cqid || data);

return [{
  json: {
    chat_id, username, text, data,
    kind, action, sku, page, arg,
    message_id, cqid, is_callback
  }
}];

/**
 * Build Menu Reply (robust, no hard node refs)
 * - Uses $input for rows (from GS: Read Menu)
 * - Safely reads context from Parse Callback (and optionally Default Menu Page)
 * - Item row + separate ‚ÑπÔ∏è row
 */

const PAGE_SIZE = 6;
const CURRENCY = '‚Çµ';
const HEADER = 'üçΩÔ∏è *Today‚Äôs Menu*';
const MAX_LABEL_CHARS = 40;

const clean = v => (v ?? '').toString().trim();
const isTrue = v => {
  const s = clean(v).toLowerCase();
  return s === 'true' || s === '1' || v === true || v === 1;
};
const priceFmt = v => {
  const n = parseFloat(v);
  return Number.isFinite(n) ? `${CURRENCY}${n.toFixed(2)}` : `${v}`;
};
const trimLabel = (s, n=MAX_LABEL_CHARS) => {
  const t = clean(s);
  return t.length > n ? t.slice(0, n - 1) + '‚Ä¶' : t;
};

// --- SAFE helper to fetch from a named node if it ran ---
function fromNode(name) {
  try {
    const a = $items(name);
    if (Array.isArray(a) && a.length) return a[0].json;
  } catch (e) {}
  return undefined;
}

// Prefer Parse Callback (always runs), else Default Menu Page, else current $json
const ctx = fromNode('Parse Callback') || fromNode('Default Menu Page') || $json;

const chat_id =
  ctx?.chat_id ??
  ctx?.message?.chat?.id ??
  ctx?.callback_query?.message?.chat?.id;

if (!chat_id) throw new Error('Build Menu Reply: no chat_id found.');

let page = Math.max(1, Number(ctx?.page || 1));

// Rows come from the previous node (GS: Read Menu) via $input
const rows = $input.all().map(i => i.json);

// Filter available
const available = rows.filter(r => {
  if ('Active' in r) return isTrue(r.Active);
  if ('Available' in r) return isTrue(r.Available);
  return true;
});

// Dedupe by SKU
const seen = new Set();
const unique = [];
for (const r of available) {
  const key = clean(r.SKU) || `${clean(r.Dish)}|${clean(r.Price)}`;
  if (!seen.has(key)) { seen.add(key); unique.push(r); }
}

// Group by Category
const groups = {};
for (const r of unique) {
  const cat = clean(r.Category) || 'Menu';
  (groups[cat] ||= []).push(r);
}
Object.values(groups).forEach(list => list.sort((a,b) => clean(a.Dish).localeCompare(clean(b.Dish))));
const catNames = Object.keys(groups).sort((a,b)=>a.localeCompare(b));

if (!catNames.length) {
  return [{
    json: {
      chat_id,
      text: `${HEADER}\n\n_No dishes available right now. Please check back later._`,
      parse_mode: 'Markdown',
      reply_markup: { inline_keyboard: [
        [{ text: 'üîÑ Refresh', callback_data: 'MENU|REFRESH' }],
        [
          { text: 'üß∫ View Cart',  callback_data: 'CART|VIEW' },
          { text: '‚úÖ Checkout',   callback_data: 'CART|CHECKOUT' },
          { text: 'üßπ Clear',      callback_data: 'CART|CLEAR' }
        ]
      ]}
    }
  }];
}

// Flatten & paginate
const flat = [];
for (const cat of catNames) {
  for (const r of groups[cat]) {
    const base = `${clean(r.Emoji) ? clean(r.Emoji) + ' ' : ''}${clean(r.Dish) || clean(r.ButtonLabel) || clean(r.SKU)}`;
    flat.push({
      sku: clean(r.SKU) || `${clean(r.Dish)}|${clean(r.Price)}`,
      cat,
      itemBtn: `${trimLabel(base)} ‚Äî ${priceFmt(r.Price)}`,
      bodyLine: `‚Ä¢ ${base} ‚Äî ${priceFmt(r.Price)}`
    });
  }
}

const totalItems = flat.length;
const totalPages = Math.max(1, Math.ceil(totalItems / PAGE_SIZE));
page = Math.min(Math.max(1, page), totalPages);
const start = (page - 1) * PAGE_SIZE;
const pageItems = flat.slice(start, start + PAGE_SIZE);

// Message body
let text = `${HEADER}\n\n`;
let lastCat = null;
for (const it of pageItems) {
  if (it.cat !== lastCat) { text += `*${it.cat}*\n`; lastCat = it.cat; }
  text += it.bodyLine + '\n';
}
text += `\n_Page ${page} of ${totalPages}_\n_Tap an item to add to cart_`;

// Keyboard: full item row + a separate ‚ÑπÔ∏è row
const kb = [];
for (const it of pageItems) {
  kb.push([{ text: it.itemBtn, callback_data: `ADD|${it.sku}` }]);
  kb.push([{ text: '‚ÑπÔ∏è',       callback_data: `DETAILS|${it.sku}` }]);
}

// Nav + utilities
const nav = [];
if (page > 1) nav.push({ text: '‚¨ÖÔ∏è Prev', callback_data: `MENU|PAGE|${page - 1}` });
nav.push({ text: 'üîÑ Refresh', callback_data: 'MENU|REFRESH' });
if (page < totalPages) nav.push({ text: 'Next ‚û°Ô∏è', callback_data: `MENU|PAGE|${page + 1}` });
kb.push(nav);
kb.push([
  { text: 'üß∫ View Cart',  callback_data: 'CART|VIEW' },
  { text: '‚úÖ Checkout',   callback_data: 'CART|CHECKOUT' },
  { text: 'üßπ Clear',      callback_data: 'CART|CLEAR' }
]);

return [{
  json: { chat_id, text, parse_mode: 'Markdown', reply_markup: { inline_keyboard: kb } }
}];

// Build Dish Details ‚Äî sendPhoto (preferred) or sendMessage (fallback)
const CURRENCY = '‚Çµ';

// Pull sku/chat_id from Parse Callback (fallback to current)
const parse = $items('Parse Callback')[0]?.json || $json;
const sku = parse.sku || $json.sku;
const chat_id = parse.chat_id || $json.chat_id;

// Rows from GS: Read Menu(details) right before this node
const rows = $input.all().map(i => i.json);
const row = rows.find(r => String(r.SKU) === String(sku));

if (!row) {
  return [{
    json: {
      __endpoint: 'sendMessage',
      payload: { chat_id, text: 'Item not found.' }
    }
  }];
}

// minimal Markdown escape for Telegram legacy Markdown
const esc = s => (s ?? '').toString().replace(/([_*[\]()~`>#+\-=|{}.!\\])/g, '\\$1');

const price = Number(row.Price) || 0;
const dish = `${esc(row.Emoji || '')} ${esc(row.Dish)} ‚Äî ${CURRENCY}${price}`;
const notes = row.Notes ? `\n_${esc(row.Notes)}_` : '';

const caption = `*${dish}*${notes}`;

// Inline keyboard: Add + Back
const kb = {
  inline_keyboard: [
    [{ text: `‚ûï Add ${esc(row.Dish)}`, callback_data: `ADD|${sku}` }],
    [{ text: '‚¨ÖÔ∏è Back', callback_data: 'MENU|REFRESH' }]
  ]
};

if (row.PhotoURL) {
  return [{
    json: {
      __endpoint: 'sendPhoto',
      payload: {
        chat_id,
        photo: row.PhotoURL,
        caption,
        parse_mode: 'Markdown',
        reply_markup: kb
      }
    }
  }];
}

// Fallback without photo
return [{
  json: {
    __endpoint: 'sendMessage',
    payload: {
      chat_id,
      text: caption,
      parse_mode: 'Markdown',
      reply_markup: kb
    }
  }
}];

// Pick Menu Item (by SKU)
// Mode: Run once for all items

// All menu rows from GS: Read Menu
const menu = $input.all().map(i => i.json);

// SKU the user actually tapped
const cb = ($items('Parse Callback')?.[0]?.json) || {};
const chat_id = String(cb.chat_id || '');
const wanted = String(cb.sku || '').trim().toUpperCase();
if (!chat_id || !wanted) return []; // nothing to do

// Find the matching row in the menu
const row = menu.find(r =>
  String(r.SKU || '').trim().toUpperCase() === wanted
);
if (!row) return []; // SKU not in menu

const price = Number(row.Price || 0);
const dish  = row.Dish ?? row.ButtonLabel ?? wanted;

// Emit exactly one new cart line
return [{
  json: {
    chat_id,
    LineId: `${chat_id}|${wanted}`,
    SKU: wanted,
    Dish: dish,
    Price: price,
    Quantity: 1,
    LineTotal: price,
    Frozen: '',
    CurrentStep: '',
    PaymentStatus: '',
    OrderNumber: '',
    OrderUID: ''
  }
}];

// Input: the single ‚Äúpicked menu item by SKU‚Äù (current add) is $json
// Read all cart rows from the previous GS: Read Cart (add)
const rows = $items('GS: Read Cart (add)').map(i => i.json);

const chatId = String($json.chat_id);
const sku    = String($json.SKU);

// ‚ÄúOpen‚Äù = NOT frozen, NOT paid, NO order number
const isOpen = r =>
  String(r.chat_id) === chatId &&
  String(r.SKU)     === sku &&
  !String(r.Frozen ?? '').toLowerCase().startsWith('t') &&
  !String(r.PaymentStatus ?? '').toUpperCase().includes('PAID') &&
  !String(r.OrderNumber ?? '').trim();

const open = rows.filter(isOpen);

// If we have an existing open row, keep its row_number to update.
// (If you want newest, sort by Timestamp and take last)
if (open.length) {
  const keep = open[open.length - 1];
  return [{
    json: {
      ...keep,
      exists: true,
      keep_row_number: keep.row_number   // <-- we‚Äôll match updates on this
    }
  }];
}

return [{
  json: {
    exists: false,
    chat_id: chatId,
    SKU: sku,
    // Carry through what your downstream nodes need
    Dish: $json.Dish,
    Price: $json.Price,
    Quantity: 1,
    LineTotal: Number($json.Price || 0)
  }
}];

// n8n Code node: Build Cart Summary (old callbacks, new formatting)
// - Keeps your original callbacks: CART|CHECKOUT, CART|CLEAR, MENU|OPEN
// - Same cart filtering (this user only, qty>0, not frozen, not PAID)
// - Just improves the *formatting* to match the screenshot style
//   ‚Ä¢ Dish xQty ‚Äî ‚ÇµPriceEach
//   Fulfillment hint + Subtotal, Delivery fee, Total
// - No Telegram code block (so no red background / ‚Äú<>‚Äù)

// ---------- helpers ----------
const NBSP = '\u00A0';
const WIDTH = 44;                      // adjust 38‚Äì50 to tune bubble width
const money = v => `‚Çµ${Number(v || 0).toFixed(2)}`;
const asInt = (v, d = 0) => {
  const n = Number(v);
  return Number.isFinite(n) ? n : d;
};
const asNum = (v, d = 0) => {
  const n = Number(v);
  return Number.isFinite(n) ? n : d;
};
// visible length (strip simple Markdown markers)
const visLen = s => String(s).replace(/\*|_/g, '').length;
const padTail = s => s + NBSP.repeat(Math.max(0, WIDTH - visLen(s)));

// ---------- context ----------
const ctxItem = $items('Parse Callback')?.[0]?.json ?? {};
const chat_id = String(ctxItem.chat_id ?? $json.chat_id ?? '');
if (!chat_id) {
  return [{
    json: { method: 'noop', payload: { reason: 'missing chat_id in Build Cart Summary' } }
  }];
}

// ---------- source rows ----------
const allRows = $input.all().map(i => i.json);

// Only active, unpaid, unfrozen rows for this user with qty > 0
const rows = allRows.filter(r => {
  if (!r || !r.SKU) return false;
  if (String(r.chat_id ?? '') !== chat_id) return false;
  const qty = asInt(r.Quantity, 0);
  if (qty <= 0) return false;
  const fr = String(r.Frozen ?? '').trim().toLowerCase();
  if (fr === 'true' || fr === '1' || fr === 'yes') return false;
  if (String(r.PaymentStatus ?? '').trim().toUpperCase() === 'PAID') return false;
  return true;
});

// ---------- empty state ----------
if (rows.length === 0) {
  const text = [
    'üß∫ *Your Cart*',
    '',
    '_Your cart is empty._',
    '',
    'Tap *View Menu* to add items.'
  ].join('\n');

  const keyboard = [
    [{ text: 'üìñ View Menu', callback_data: 'MENU|OPEN' }]
  ];

  return [{
    json: {
      method: 'sendMessage',
      payload: {
        chat_id,
        text,
        parse_mode: 'Markdown',
        reply_markup: { inline_keyboard: keyboard }
      }
    }
  }];
}

// ---------- build (formatting only) ----------
let subtotal = 0;

const itemLines = rows.map(r => {
  const dish  = String(r.Dish ?? r.SKU ?? '‚Äî');
  const qty   = asInt(r.Quantity, 0);
  const price = asNum(r.Price, 0);          // per-item price (as in screenshot)
  subtotal += qty * price;

  // ‚Ä¢ Waakye Set x1 ‚Äî ‚Çµ65.00
  return padTail(`‚Ä¢ ${dish} x${qty} ‚Äî ${money(price)}`);
});

const deliveryFee = 0;                       // cart view shows 0.00; checkout will compute later
const total = subtotal + deliveryFee;

// ---------- keyboard (unchanged: your old callbacks) ----------
const keyboard = [
  [{ text: 'üßæ Checkout', callback_data: 'CART|CHECKOUT' }],
  [
    { text: 'üßπ Clear cart', callback_data: 'CART|CLEAR' },
    { text: 'üìñ View Menu',  callback_data: 'MENU|OPEN'  }
  ]
];

// ---------- final text ----------
const text = [
  padTail('üß∫ *Your Cart*'),
  '',
  ...itemLines,
  '',
    '',
  padTail(`*Subtotal:* ${money(subtotal)}`),
  padTail(`*Delivery fee:* ${money(deliveryFee)}`),
  padTail(`*Total:* ${money(total)}`)
].join('\n');

// ---------- emit ----------
return [{
  json: {
    method: 'sendMessage',
    payload: {
      chat_id,
      text,
      parse_mode: 'Markdown',
      reply_markup: { inline_keyboard: keyboard }
    }
  }
}];

// Collect Rows To Delete ‚Äî delete ONLY unpaid & unfrozen lines for this user

const ctx = $items('Parse Callback')?.[0]?.json ?? {};
const chat_id = String(ctx.chat_id ?? $json.chat_id ?? '');

const rows = $input.all().map(i => i.json).filter(r => String(r.chat_id ?? '') === chat_id);

// helpers
function isPaid(v) {
  const s = String(v ?? '').trim().toLowerCase();
  // allow common synonyms/flags
  return ['paid','success','successful','confirmed','complete','completed','true','yes','1'].includes(s);
}
function isTrue(v) {
  const s = String(v ?? '').trim().toLowerCase();
  return ['true','1','yes'].includes(s);
}

// keep only rows we should delete: NOT paid and NOT frozen
const toDelete = rows
  .filter(r => !isPaid(r.PaymentStatus) && !isTrue(r.Frozen))
  .map(r => Number(r.row_number))
  .filter(n => Number.isFinite(n))
  // delete bottom-up to avoid row-shift
  .sort((a, b) => b - a);

if (toDelete.length === 0) {
  // Signal "nothing to delete" to your IF node
  return [{ json: { chat_id, none: true } }];
}

// emit one item per row_number
return toDelete.map(n => ({ json: { chat_id, row_number: n } }));

