parameters.jsCode:
// Fan out dupes array to items with row_number
const chat_id = $json.chat_id;
const dupes = Array.isArray($json.dupes) ? $json.dupes : [];
if (!dupes.length) return [];
return dupes.map(n => ({ json: { chat_id, row_number: Number(n) } }));

/**
 * Build Checkout Summary (Delivery/Pickup aware)
 * Input: items from "Attach State ‚Üí Lines (checkout)" (cart lines with state fields attached)
 * Output: { method, payload } for Telegram
 */

const CURRENCY = '‚Çµ';
const WIDTH = 40;
const NBSP = '\u00A0';

const money  = n => `${CURRENCY}${(Number(n)||0).toFixed(2)}`;
const visLen = s => s.replace(/\*|_/g,'').length;
const pad    = s => s + NBSP.repeat(Math.max(0, WIDTH - visLen(s)));

const rows = $input.all().map(i => i.json);

// Robust chat_id
const chat_id = String(
  rows[0]?.chat_id ?? rows[0]?.ChatID ?? $json._chat_id ?? $json.chat_id ?? ''
);

if (!rows.length || !chat_id) {
  return [{
    json: {
      method: 'sendMessage',
      payload: {
        chat_id,
        text: 'üß∫ Your cart is empty.\n\nTap *Menu* to add items.',
        parse_mode: 'Markdown',
        reply_markup: { inline_keyboard: [[{ text: 'üçΩÔ∏è Back to Menu', callback_data: 'MENU|REFRESH' }]] }
      }
    }
  }];
}

// State context (now present on each line)
const fulfillmentRaw = String(rows[0]?.Fulfillment || '').toLowerCase().trim();
const address  = String(rows[0]?.Address  || '').trim();
const zone     = String(rows[0]?.Zone     || '').trim();
const phone    = String(rows[0]?.Phone    || '').trim();
const feeInput = Number(rows[0]?.DeliveryFee ?? 0);
const isPickup   = fulfillmentRaw === 'pickup';
const isDelivery = fulfillmentRaw === 'delivery';
const fee        = isPickup ? 0 : (Number.isFinite(feeInput) ? feeInput : 0);

// Lines
const header = pad('üß∫ *Your Cart*');
const lines = rows.map(r => {
  const qty   = Number(r.Quantity ?? r.qty ?? 1) || 1;
  const price = Number(r.Price ?? r.price ?? 0)  || 0;
  const name  = String(r.Dish ?? r.name ?? r.SKU ?? '').trim();
  return pad(`‚Ä¢ ${name} x${qty} ‚Äî ${money(price)}`);
});

const subtotal = rows.reduce((s, r) => {
  const lt = Number(r.LineTotal ?? r.line_total);
  if (Number.isFinite(lt) && lt > 0) return s + lt;
  const q = Number(r.Quantity ?? r.qty ?? 1) || 1;
  const p = Number(r.Price ?? r.price ?? 0)  || 0;
  return s + q * p;
}, 0);

const fulfillmentLine = isPickup
  ? 'üè™ Pickup at counter'
  : (isDelivery
     ? `üöö Delivery${address ? ` to: ${address}` : ''}${zone ? ` (Zone: ${zone})` : ''}`
     : '‚Äî choose delivery or pickup below ‚Äî');

const meta = [
  pad(`*Fulfillment:* ${fulfillmentLine}`),
  phone ? pad(`*Phone:* ${phone}`) : null
].filter(Boolean);

const foot = [
  pad(`*Subtotal:* ${money(subtotal)}`),
  pad(`*Delivery fee:* ${money(fee)}`),
  pad(`*Total:* ${money(subtotal + fee)}`)
];

const text = [header, '', ...lines, '', ...meta, '', ...foot].join('\n');

// Keyboard
const baseRows = [
  [
    { text: 'üßπ Clear', callback_data: 'CART|CLEAR' },
    { text: 'üçΩÔ∏è Continue Shopping', callback_data: 'MENU|REFRESH' }
  ]
];

let keyboard;
if (!isPickup && !isDelivery) {
  keyboard = [
    [
      { text: 'üöö Delivery', callback_data: 'FULFILL|DELIVERY' },
      { text: 'üè™ Pickup',   callback_data: 'FULFILL|PICKUP' }
    ],
    ...baseRows
  ];
} else if (isPickup) {
  keyboard = [
    [{ text: 'üí≥ Pay now', callback_data: 'PAY' }],
    [{ text: 'Change to Delivery', callback_data: 'FULFILL|DELIVERY' }],
    ...baseRows
  ];
} else {
  // delivery
  if (address) {
    keyboard = [
      [{ text: 'üí≥ Pay now', callback_data: 'PAY' }],
      [{ text: 'Change to Pickup', callback_data: 'FULFILL|PICKUP' }],
      ...baseRows
    ];
  } else {
    keyboard = [
      [{ text: 'üìç Add address', callback_data: 'ADDR|CHANGE' }],
      [{ text: 'Change to Pickup', callback_data: 'FULFILL|PICKUP' }],
      ...baseRows
    ];
  }
}

