// 2) Otherwise mint a fresh short, low-collision ref
const chatId = (items[0]?.chat_id ?? '') + '';
function makeRef(chatIdStr) {
  const t = Date.now(); // ms
  // 4 chars of rolling time + 3 chars of a tiny hash of (chat_id + t) => 7 chars total
  const time4 = (t % (36 ** 4)).toString(36).toUpperCase().padStart(4, '0');
  let h = 0; const s = chatIdStr + '|' + t;
  for (let i = 0; i < s.length; i++) h = ((h * 31) + s.charCodeAt(i)) >>> 0;
  const tail3 = (h % (36 ** 3)).toString(36).toUpperCase().padStart(3, '0');
  return `SFK-${time4}${tail3}`; // e.g., SFK-7Q9KX1J (7 after dash)
}

const ref = makeRef(chatId);
return [{ json: { OrderNumber: ref, OrderUID: ref } }];

